import React, { Component } from "react";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import { Link } from "react-router-dom";
import { withFirebase } from "../Firebase";
import * as ROUTES from "../../constants/routes";
import { compose } from "recompose";
import { withStyles } from "@material-ui/core/styles";
import styles from "../../constants/styles";

const PasswordForgetPage = () => (
  <div>
    <Typography variant="h3">Forgot your password?</Typography>
    <PasswordForgetForm />
  </div>
);

const INITIAL_STATE = {
  email: "",
  error: null,
};

class PasswordForgetFormBase extends Component {
  constructor(props) {
    super(props);
    this.state = { ...INITIAL_STATE };
  }

  onSubmit = (event) => {
    this.props.firebase.doPasswordReset().catch((error) => {
      this.setState({ error });
    });

    event.preventDefault();
  };

  render() {
    const { error } = this.state;
    const { classes } = this.props;

    return (
      <div>
        Send a verification email so you can reset your password.
        <br />
        <br />
        <form onSubmit={this.onSubmit}>
          <Button
            type="submit"
            variant="contained"
            className={classes.primaryLightButton}
          >
            Reset Password
          </Button>

          {error && <p>{error.message}</p>}
        </form>
      </div>
    );
  }
}

const PasswordForgetLinkBase = (props) => {
  // <p>
  //   <Link to={ROUTES.PASSWORD_FORGET}>Forgot Password?</Link>
  // </p>
  const { classes } = props;
  return (
    <p>
      Forgot password? Send a verification email and get it reset
      <Button className={classes.primaryLightButton}>here</Button>.
    </p>
  );
};

export default PasswordForgetPage;

const PasswordForgetForm = compose(
  withStyles(styles),
  withFirebase
)(PasswordForgetFormBase);

const PasswordForgetLink = withStyles(styles)(PasswordForgetLinkBase);
export { PasswordForgetForm, PasswordForgetLink };
