import React from "react";
import styles from "../../constants/styles";
import Button from "@material-ui/core/Button";
import { withStyles } from "@material-ui/core/styles";
import { withFirebase } from "../Firebase";
import { compose } from "recompose";

class DeleteAccount extends React.Component {
  constructor(props) {
    super(props);

    this.state = { showDialog: false };
  }

  onDeleteAccount = () => {};

  render() {
    const { classes, showDialog } = this.props;
    return (
      <div>
        Permanantly delete your account. This action cannot be reversed.
        <br />
        <br />
        <Button
          className={classes.primaryLightButton}
          onClick={this.onDeleteAccount}
        >
          Delete Account
        </Button>
      </div>
    );
  }
}

export default compose(withFirebase, withStyles(styles))(DeleteAccount);
