import React from "react";
import { withFirebase } from "../Firebase";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import styles from "../../constants/styles";
import { withStyles } from "@material-ui/core/styles";
import { compose } from "recompose";

const onSubmit = (values, dispatch) => {
  e.preventDefault();
  var oldEmail = document.getElementById("oldEmail").value;
  var newEmail = document.getElementById("newEmail").value;
};

const EmailChange = (props) => {
  const { classes } = props;
  return (
    <div>
      <div>
        Change the email that your account is linked to. If you forget your
        password, you'll receive any emails from this email instead.
      </div>
      <br />
      <form onSubmit={onSubmit}>
        <TextField id="oldEmail" label="Old Email Address" />
        <br />
        <TextField id="newEmail" label="New Email Address" />
        <br />
        <br />
        <Button
          type="submit"
          variant="contained"
          className={classes.primaryLightButton}
        >
          Change Email
        </Button>
      </form>
    </div>
  );
};

export default compose(withStyles(styles), withFirebase)(EmailChange);
