import React, { Component } from "react";
import { withFirebase } from "../Firebase";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { compose } from "recompose";
import { withStyles } from "@material-ui/core/styles";
import styles from "../../constants/styles";
import { Console } from "console";

const INITIAL_STATE = {
  passwordOne: "",
  passwordTwo: "",
  currentPassword: "",
  error: null,
};

class PasswordChangeForm extends Component {
  constructor(props) {
    super(props);
    this.state = { ...INITIAL_STATE, updated: false, success: "" };
  }

  verifyCurrentPassword = async (currentPassword) => {
    await this.props.firebase.doCurrentPasswordVerification(currentPassword);
  };

  updateCurrentPassword = async (password) => {
    await this.props.firebase.doPasswordUpdate(password);
  };

  managePasswordActions = async (newPass1, newPass2, oldPass) => {
    await this.verifyCurrentPassword(oldPass)
      .then((res) => {
        console.log("MANAGE PASSWORD ACTIONS");
        console.log(res);
        if (newPass1 !== newPass2) {
          console.log("DIFFERENT NEW PASSWORDS");
          this.setState({
            error: "The two passwords are different from each other.",
          });
        }
        // return this.updateCurrentPassword(newPass1);
      })
      .catch((e) => {
        return e;
      });
  };

  onSubmit = async (e) => {
    e.preventDefault();
    // this.setState({ error: "" });
    const { passwordOne, passwordTwo, currentPassword } = this.state;
    console.log("BEFORE RESPONSE");
    await this.managePasswordActions(
      passwordOne,
      passwordTwo,
      currentPassword
    )
  };

  onChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  render() {
    const {
      currentPassword,
      passwordOne,
      passwordTwo,
      error,
      success,
    } = this.state;
    const { classes } = this.props;
    return (
      <div>
        <div>
          Change the email that your account is linked to. If you forget your
          password, you'll receive any emails from this email instead.
        </div>
        <br />
        <form onSubmit={this.onSubmit}>
          <TextField
            name="currentPassword"
            value={currentPassword}
            onChange={this.onChange}
            type="password"
            label="Original Password"
          />
          <br />
          <TextField
            name="passwordOne"
            value={passwordOne}
            onChange={this.onChange}
            type="password"
            label="New Password"
          />
          <br />
          <TextField
            name="passwordTwo"
            value={passwordTwo}
            onChange={this.onChange}
            type="password"
            label="Confirm New Password"
          />
          <br />
          <br />
          <Button
            type="submit"
            className={classes.primaryLightButton}
            variant="contained"
          >
            Reset Password
          </Button>

          {error && <p>{error}</p>}
          {success && <p>{success}</p>}
        </form>
      </div>
    );
  }
}

// export default withFirebase(PasswordChangeForm);
export default compose(withFirebase, withStyles(styles))(PasswordChangeForm);
