import React, { Component } from "react";
import { withFirebase } from "../Firebase";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { compose } from "recompose";
import { withStyles } from "@material-ui/core/styles";
import styles from "../../constants/styles";
import { exception } from "console";
import { verify } from "crypto";

const INITIAL_STATE = {
  passwordOne: "",
  passwordTwo: "",
  currentPassword: "",
  error: null,
};

class PasswordChangeForm extends Component {
  constructor(props) {
    super(props);
    this.state = { ...INITIAL_STATE, updated: false, success: false };
  }

  onSubmit = async (e) => {
    e.preventDefault();

    const { passwordOne, passwordTwo, currentPassword } = this.state;

    if (passwordOne !== passwordTwo) {
      this.setState({
        error: { message: "The two passwords are different from each other." },
      });
      return;
    }

    let verifyErr = await this.props.firebase.doCurrentPasswordVerification(
      currentPassword
    );
    console.log("VERIFY ERR: " + verifyErr + "\t" + typeof verifyErr);
    if (verifyErr) {
      this.setState({ error: verifyErr });
      return;
    }

    let updateErr = this.props.firebase.doPasswordUpdate(passwordOne);
    console.log("UPDATE ERR: " + updateErr);
    if (updateErr) {
      this.setState({ error: updateErr });
    } else {
      this.setState({ success: true, passwordOne: "", passwordTwo:"",  });
    }
  };

  onChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  render() {
    const { currentPassword, passwordOne, passwordTwo, error } = this.state;
    const { classes } = this.props;
    return (
      <div>
        <div>
          Change the email that your account is linked to. If you forget your
          password, you'll receive any emails from this email instead.
        </div>
        <br />
        <form onSubmit={this.onSubmit}>
          <TextField
            name="currentPassword"
            value={currentPassword}
            onChange={this.onChange}
            type="password"
            label="Original Password"
          />
          <br />
          <TextField
            name="passwordOne"
            value={passwordOne}
            onChange={this.onChange}
            type="password"
            label="New Password"
          />
          <br />
          <TextField
            name="passwordTwo"
            value={passwordTwo}
            onChange={this.onChange}
            type="password"
            label="Confirm New Password"
          />
          <br />
          <br />
          <Button
            type="submit"
            className={classes.primaryLightButton}
            variant="contained"
          >
            Reset Password
          </Button>

          {error && <p>{error.message}</p>}
        </form>
      </div>
    );
  }
}

// export default withFirebase(PasswordChangeForm);
export default compose(withFirebase, withStyles(styles))(PasswordChangeForm);
