import React from "react";
import styles from "../../constants/styles";
import Button from "@material-ui/core/Button";
import { withStyles } from "@material-ui/core/styles";
import { withFirebase } from "../Firebase";
import { compose } from "recompose";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";

class DeleteAccount extends React.Component {
  constructor(props) {
    super(props);

    this.state = { showDialog: false };
  }

  onDeleteAccount = () => {};

  handleCancel = () => {
    this.setState({ showDialog: false });
  };

  handleOpen = () => {
    this.setState({ showDialog: true });
  };

  render() {
    const { classes } = this.props;
    const { showDialog } = this.state;
    return (
      <div>
        Permanantly delete your account. This action cannot be reversed.
        <br />
        <br />
        <Button
          className={classes.primaryLightButton}
          onClick={this.handleOpen}
          variant="contained"
        >
          Delete Account
        </Button>
        <Dialog open={showDialog} onClose={this.handleCancel}>
          <DialogTitle id="alert-dialog-title">
            {"Delete your account permanatly?"}
          </DialogTitle>
          <DialogContent>
            <DialogContentText id="alert-dialog-description">
              Let Google help apps determine location. This means sending
              anonymous location data to Google, even when no apps are running.
            </DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button onClick={this.handleCancel} color="primary">
              Disagree
            </Button>
            <Button onClick={this.onDeleteAccount} color="primary" autoFocus>
              Agree
            </Button>
          </DialogActions>
        </Dialog>
      </div>
    );
  }
}

export default compose(withFirebase, withStyles(styles))(DeleteAccount);
