const styles = (theme) => ({
  root: {
    flexGrow: 1,
  },
  title: {
    flexGrow: 1,
    textDecoration: "none",
    color: "white",
  },
  right: {
    underline: "none",
    color: "white",
    textDecoration: "none",
    margin: "0 2vw",
  },
  appbar: {
    backgroundColor: "#2E3B55",
  },
  primaryLightButton: {
    backgroundColor: theme.palette.primary.light,
    "&:hover": {
      background: theme.palette.primary.lightHover,
    },
    margin: theme.spacing(0.5),
    borderRadius: "3em",
    textTransform: "none",
    color: "white",
  },
});

export default styles;
